import React from "react";
import axios from "axios";

import "./App.css";

class App extends React.Component {
  state = {
    Bible: "",
  };

  componentDidMount() {
    this.fetchAdvice();
  }

  fetchAdvice = () => {
    axios
      .get("https://api.adviceslip.com/advice")
      .then((response) => {
        const { advice } = response.data.slip;

        this.setState({ advice });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { advice } = this.state;

    return (
      <div className="app">
        <div className="card">
          <h1 className="headinng">{advice}</h1>

          <button>
            <span>
              GIVE ME ADVICE!
            </span>
          </button>
        </div>
      </div>
    );
  }
}

export default App;
